"use strict";document.addEventListener("DOMContentLoaded",function(){var n=new Scroller("#root");document.addEventListener("wheel",function(e){return n.listenScroll(e)}),document.addEventListener("swipeUp",function(){return n.scroll(1)}),document.addEventListener("swipeDown",function(){return n.scroll(-1)}),document.addEventListener("keydown",function(e){switch(e.keyCode){case 40:return n.scroll(1);case 38:return n.scroll(-1);default:return}})});